function inject_unit_test_related_values(Array $default_theme_options)
{
    $default_theme_options['side_panel_enable'] = false;
    $default_theme_options['blog_list_read_more'] = false;
    $default_theme_options['footer_spacing']['padding-top'] = '100px';
    $default_theme_options['footer_spacing']['padding-bottom'] = '50px';

    return $default_theme_options;
}

add_action('after_switch_theme', 'bighearts_get_default_option');
add_action('after_setup_theme', function() {
    $version = '1.0.0';
    if (get_option('bighearts_option_version') != $version) {
        bighearts_get_default_option();
        update_option('bighearts_option_version', $version);
    }
});

function bighearts_fonts_url()
{
    /* Translators: If there are characters in your language that are not supported
     * by Quicksand, translate this to 'off'. Do not translate into your own language.
     */
    $quicksand = esc_html_x('on', 'Quicksand: on or off', 'bighearts');

    /* Translators: If there are characters in your language that are not supported
     * by Nunito Sans, translate this to 'off'. Do not translate into your own language.
     */
    $nunito_sans = esc_html_x('on', 'Nunito Sans: on or off', 'bighearts');

    /* Translators: If there are characters in your language that are not supported
     * by Amatic SC, translate this to 'off'. Do not translate into your own language.
     */
    $amatic_sc = esc_html_x('on', 'Amatic SC: on or off', 'bighearts');

    if ('off' !== $amatic_sc || 'off' !== $nunito_sans || 'off' !== $quicksand) {
        $font_families = [];

        if ('off' !== $quicksand) $font_families[] = 'Quicksand:400,500,600,700';
        if ('off' !== $nunito_sans) $font_families[] = 'Nunito Sans:400,600,700';
        if ('off' !== $amatic_sc) $font_families[] = 'Amatic SC:400,700';

        $query_args = [
            'family' => urlencode(implode('|', $font_families)),
            'subset' => urlencode('latin,latin-ext'),
        ];

        $fonts_url = add_query_arg($query_args, 'https://fonts.googleapis.com/css');
    }

    return esc_url_raw($fonts_url ?? '');
}

add_action('admin_enqueue_scripts', function() {
    wp_enqueue_style(
        'editor-google-fonts',
        bighearts_fonts_url(),
        [],
        WglAddons\Bighearts_Global_Variables::get_theme_version()
    );
});

if (!class_exists('BigHearts_CorePlugin')) {
    add_action('wp_enqueue_scripts', function () {
        wp_enqueue_style(
            'bighearts-default-font',
            bighearts_fonts_url(),
            [],
            WglAddons\Bighearts_Global_Variables::get_theme_version()
        );
    });
}
